import React, { useState } from "react";
import { shortenUrl } from "../../services/api";
import { Button, ShortenedLink } from "../common";

const Shortener = () => {
  const [url, setUrl] = useState("");
  const [shortenedLinks, setShortenedLinks] = useState([]);

  const handleShortenUrl = async (url) => {
    if (!url) {
      return;
    }
    const shortenedUrl = await shortenUrl(url);
    if (shortenedUrl) {
      setShortenedLinks([...shortenedLinks, shortenedUrl]);
    }
  };

  return (
    <div className="shortener">
      <div className="shortener-input">
        <input
          type="text"
          value={url}
          placeholder="Shorten a link here..."
          onChange={(event) => setUrl(event.target.value)}
        />
        <Button action={() => handleShortenUrl(url)}>Shorten It!</Button>
      </div>
      <div className="shortener-output">
        {shortenedLinks.length > 0 &&
          shortenedLinks
            .slice(0, 3)
            .map((link, index) => <ShortenedLink key={index} link={link} />)}
      </div>
    </div>
  );
};

export default Shortener;
